const execSync = require('child_process').execSync;

/*
* shellExec executes a shell command and returns the stdout, in case of errors return the error
* @param: string - contains the command to execute
* @return: string - the stdout of the command executed
*/

exports.shellExec = function (command) {
  try {
    return execSync(command).toString();
  }
  catch(err) {
    return err.toString();
  }
}

